import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
  width : 100%;
  height : 100%;
  flex : 1;
  margin: 1%;
  background-color: cadetblue;
  display: flex;

`;

const Title = styled.h1`
  font-size: 2em;
  font-weight: 500;
`;

const Img = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;


export default function CategoryItem({ title, img }) {
  return (
    <Container>
      <Img src={img} />
    </Container>
  )
}
