import styled from 'styled-components/';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import Products from '../components/Products';
import NewsLetter from '../components/NewsLetter';
import { useLocation } from 'react-router-dom';
import { useState } from 'react';


const PageContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  `

const FiltersContainer = styled.div`
  display: flex;
  align-items: center;
  height: 80px;
  width: 100%;
  justify-content: space-between;
  background-color: aliceblue;
  
`

const PageTitle = styled.h1`
  color: #3a3a3a;
  font-size: 3em;
  font-weight: 300;
  margin: 1% 0 2% 2%;
`;

const FilterTypeContainer = styled.div`
  display: flex;
  align-items: center;
  margin: 0 3%;

`
const Text = styled.span`
  font-size: 1.5em;
`
const Selection = styled.select`
width: auto;
font-size: 1em;
height: auto;
margin-left: 1em;
padding: 10px;
`
const Option = styled.option`
  font-size: 1em;
`


function ProductsList() {
  const location = useLocation();
  console.log(location.pathname.split("/")[2]);

  const [filter, setFilter] = useState({})

  const handleSelect = (e) => {
    setFilter({
      ...filter,
      [e.target.name]: e.target.value
    }
    )
  }
  console.log(filter)

  return (
    <PageContainer>
      <Navbar />
      <PageTitle>Products</PageTitle>
      <FiltersContainer>
        <FilterTypeContainer>
          <Text>Filter Products</Text>
          <Selection name='filter' onChange={handleSelect}>
            <Option disabled selected >Type</Option>
            <Option >Coats</Option>
            <Option >Jackets</Option>
            <Option >Sweaters</Option>
          </Selection>
          <Selection name='size' onChange={handleSelect}>
            <Option disabled selected >Type</Option>
            <Option >S</Option>
            <Option >M</Option>
            <Option >L</Option>
          </Selection>
        </FilterTypeContainer>

        <FilterTypeContainer>
          <Text>Sort Products</Text>
          <Selection name='sorting' onChange={handleSelect} >
            <Option disabled selected >Newest</Option>
            <Option >Price (asc)</Option>
            <Option >Price (desc)</Option>
            <Option >Newest</Option>
          </Selection>
        </FilterTypeContainer>

      </FiltersContainer>
      <Products />
      <NewsLetter />
      <Footer />

    </PageContainer>
  );
}

export default ProductsList;
