import styled from 'styled-components';
import Product from './Product';
import { productsData } from '../data/productsData';
import { mobile } from '../data/responsive';
import { useEffect, useState } from 'react';


const PageWrapper = styled.div`
  margin-top: 1%;
  width : 100%;
  height : 100vh;
  display : flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  ${mobile({ height: "auto" })};

  
  `;

const Container = styled.div`
  width : 100%;
  height : 100%;
  display : flex;
  //padding : 20px;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-between;
  ${mobile({ flexDirection: "column", flexWrap: 'nowrap' })};
`;


export default function Products({ filters, sort, category }) {

  const [filter, setFilter] = useState({ filters })

  useEffect(() => {
    setFilter(filters)
  }, [filters])

  return (
    <PageWrapper>
      {console.log(filter.filter)}
      <Container>
        {productsData.filter(el => el.title === filters.filter)
          .map(el => <Product item={el} key={el.id} />)}
      </Container>
    </PageWrapper>

  )
}
