import React, { useEffect } from 'react'
import styled from 'styled-components'
import Footer from '../components/Footer'
import Navbar from '../components/Navbar'
import { useState } from 'react'
import NewsLetter from '../components/NewsLetter'
import { useDispatch, useSelector } from 'react-redux'
import { addProduct } from '../redux/cartRedux'
import StripeCheckout from 'react-stripe-checkout'
import axios from 'axios'


const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`
const Title = styled.h1`
  font-size: 2em;
  margin-top: 2%;
  font-weight: 400;
`
const BtnsWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 95%;
  margin-bottom: 2%;
`
const BodyContainer = styled.div`
  display: flex;
  width: 95%;
  height: auto;
`
const ItemsContainer = styled.div`
  flex: 3;
  display: flex;
  flex-direction: column;
  //background-color: #d8e2e2;
  height: auto;
`

const OrderContainer = styled.div`
  flex: 1;
  display: flex;
  height: 500px;
  border: 1px solid grey;
  border-radius: 20px;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`

const OrderWrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 90%;
  height: 90%;
`

const Item = styled.div`
  display: flex;
  align-items: center;
  border-bottom: 1px solid grey;
  padding: 20px 0;
  position: relative;
`
const Image = styled.img`
  width: 250px;
  height: 250px;
  object-fit: contain;
`
const DescContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  height: 80%;
  width: auto;
`

const PriceAndAmountContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  height: 50%;
  width: 10%;
  position: absolute;
  right: 5%;
`
const AmountContainer = styled.div`
  display: flex;
  justify-content: space-between;
  height: 80%;
  width: 100%;
`
const DescSpan = styled.span`
  font-size: 1.2em;
`
const SpanContainer = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  justify-content: space-between;
  font-size: ${props => props.fontsize};

`

const Amount = styled.p`
  font-size: 1.5em;
  margin: 0 3%;
`
const Price = styled.span`
  font-size: 2em;
  font-weight: 500;
`

const SymbolBtn = styled.button`
  font-size: 1.5em;
  display: flex;
  align-items: center;
  justify-content: center;
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  transition: all 0.2s ease;
  &:hover {
    background-color: #303030;
    color: white;
  border: 2px solid white;
  }
`

const Btn = styled.button`
  background: white;
  cursor : pointer;
  color : black;
  border: 2px solid grey;
  padding: .8em;
  width: 200px;
  font-size: .8em;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  &:hover {
    background-color: #303030;
    color: white;
  border: 2px solid white;
  }

`;

const SummaryTitle = styled.h2`
  font-size: 2.5em;
  font-weight: 400;
  color: #252525;
  margin-bottom: 10%;
`

const CheckoutBtn = styled.button`
  background: #000000;
  cursor : pointer;
  color : #dddddd;
  border: 2px solid grey;
  font-weight: 500;
  margin-top: 10%;
  padding: .8em;
  width: 200px;
  font-size: 1em;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  &:hover {
    background-color: #ffffff;
    color: #252525;
  border: 2px solid grey;
  }

`;


const KEY = process.env.STRIPE_KEY


export default function Cart() {


  const [amount, setAmount] = useState(1);
  const [limit, setLimit] = useState(false);
  const cart = useSelector(state => state.cart)


  const handleClick = (action) => {
    if (action === "decrease" && amount > 1) {
      setAmount(amount - 1);
      setLimit(false);
      updateCartAmount();
    }
    if (action === "increase" && amount === 5) {
      setLimit(true);
    }
    else if (action === "increase" && amount < 5) {
      setAmount(amount + 1);
      updateCartAmount();

    }
  }

  const dispatch = useDispatch();
  const updateCartAmount = () => {
    dispatch(
      addProduct({ amount })
    );
  }

  const [stripeToken, setStripeToken] = useState(null)

  const onToken = (token) => {
    setStripeToken(token);
    console.log(token)
  }

  // useEffect(() => {
  //   const makeRequest = async () => {
  //     try {
  //       const res = await axios.post('http://localhost:5000/api/checkout/payment')
  //       console.log(res.data)

  //     } catch (err) {
  //       console.log(err);
  //     }
  //   }
  //   stripeToken && makeRequest()
  // }, [stripeToken])


  return (
    <Container>
      <Navbar />
      <Title>Cart Products</Title>
      <BtnsWrapper>
        <Btn>Continue Shopping</Btn>
        <Btn>Go To Checkout</Btn>
      </BtnsWrapper>
      <BodyContainer>
        <ItemsContainer>
          {cart.products?.map((el) =>

          (<Item key={el._id} >
            <Image src={el.img} />
            <DescContainer>
              <DescSpan>
                <b>PRODUCT:</b> {el.title}
              </DescSpan>
              <DescSpan>
                <b>ID:</b> {el._id}
              </DescSpan>
              <DescSpan>
                <b>COLOR:</b> {el.color}
              </DescSpan>
              <DescSpan>
                <b>SIZE:</b> {el.size}
              </DescSpan>
            </DescContainer>
            <PriceAndAmountContainer>
              <AmountContainer>
                <SymbolBtn onClick={() => handleClick("decrease")} >-</SymbolBtn>
                <Amount>{el.amount}</Amount>
                <SymbolBtn onClick={() => handleClick("increase")} >+</SymbolBtn>
              </AmountContainer>
              <Price>$ {el.price}</Price>
            </PriceAndAmountContainer>
          </Item>)
          )}


        </ItemsContainer>
        <OrderContainer>
          <OrderWrapper>
            <SummaryTitle>ORDER SUMMARY</SummaryTitle>
            <DescContainer>
              <SpanContainer>
                <DescSpan>Subtotal</DescSpan>
                <DescSpan>${cart.total}</DescSpan>
              </SpanContainer>
              <SpanContainer>
                <DescSpan>Estimated Shipping</DescSpan>
                <DescSpan>$5</DescSpan>
              </SpanContainer>
              <SpanContainer>
                <DescSpan>Shipping Discount</DescSpan>
                <DescSpan>$-5</DescSpan>
              </SpanContainer>
              <SpanContainer fontsize="2em">
                <DescSpan>Total</DescSpan>
                <DescSpan>$ {cart.total}</DescSpan>
              </SpanContainer>
            </DescContainer>
            <StripeCheckout
              name='E-Commerce Shop'
              billingAddress
              shippingAddress
              description='Your total is'
              amount={cart.total * 100}
              token={onToken}
              stripeKey={KEY}
            >
              <CheckoutBtn>Check Out</CheckoutBtn>
            </StripeCheckout>
          </OrderWrapper>
        </OrderContainer>
      </BodyContainer>
      <NewsLetter />
      <Footer />
    </Container>
  )
}
