import React from 'react'
import styled from 'styled-components'
import { ArrowLeft, ArrowRight } from '@material-ui/icons/';


const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
`
const Arrow = styled.div`
  position: absolute;
  top: 0;
  bottom: 0;
  margin: auto;
  height: 50px;
  width: 50px;
  display:flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  background-color: #e4e4e4;
  left: ${props => props.direction === "left" && "20px"};
  right: ${props => props.direction === "right" && "20px"};
  cursor:pointer;
  opacity: 0.5;
`
const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
`
const ImgContainer = styled.div`
  flex: 1;
  background-color: coral;
`
const Img = styled.img`
  height: 80%;
`
const TextContainer = styled.div`
  flex: 1;
  background-color: #836d66;
  display: flex;
  flex-direction: column;
  justify-content: center;
`
const Title = styled.h1`
  font-size: 5em;
  color: #2c2c2c;
  margin-bottom: 10%;

`
const Paragraph = styled.p`
  font-size: 1.8em;
  margin-bottom: 10%;
  margin-right: 10%;

`
const ShowButton = styled.button`
  background: none;
  cursor : pointer;
  border: 2px solid grey;
  padding: 1%;
  width: 15%;
  font-size: 1.5em;

`

export default function Slider() {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowLeft />
      </Arrow>
      <Arrow direction="right">
        <ArrowRight />
      </Arrow>
      <Wrapper>
        <ImgContainer>
          <Img src="/images/slider/slider_coat_orange.png" />
        </ImgContainer>

        <TextContainer>
          <Title>Summer Sales</Title>
          <Paragraph>
            Shop now summer Articles for this week only 20% discount on all products.
          </Paragraph>
          <ShowButton>
            Show More
          </ShowButton>
        </TextContainer>
      </Wrapper>
    </Container>
  )
}
