  display: flex;
  align-items:center;
  justify-content: center;
  flex-direction: column;
  width: 100%;
  height: auto;
`;

const PageTitle = styled.h1`
  color: #3a3a3a;
  font-size: 3em;
  font-weight: 300;
  margin: 8% 0 2% 0;
`;


const FiltersContainer = styled.div`
  display: flex;
  
`
const SortContainer = styled.dev`
  display: flex;

`
const Text = styled.span`
  font-size: 2em;
`
const Selection = styled.select`
  
`



function ProductsList() {
  return (
    <div>
      <Navbar />
      <Wrapper>
        <Products />
      </Wrapper>
      <NewsLetter />
      <Footer />

    </div>
  );
}

export default ProductsList;
