import { useEffect } from "react";
import { useLocation } from "react-router";

const ScrollToTop = (props) => {
  const location = useLocation();


  useEffect(() => {
    window.onbeforeunload = () => {

      window.scrollTo(0, 0);
    }
  }, [location]);



  // useEffect(() => {
