import styled from 'styled-components/';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import Products from '../components/Products';
import NewsLetter from '../components/NewsLetter';
import { useState } from 'react';


const PageContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  `
const Wrapper = styled.div`
  display: flex;
  width: 100%;
  height: 85vh;
`

const Left = styled.div`
  flex: 1;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: aliceblue;
  
`
const Right = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
`
const RightContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  height: 100%;

`

const ProductImg = styled.img`
  object-fit: contain;
  width: auto;
  height: 90%;
`


const BlockContainer = styled.div`
  display: flex;
  align-items: center;
  height: 80px;
  margin-top: 2%;
  width: 100%;
  justify-content: space-between;
  
`

const PageTitle = styled.h1`
  color: #3a3a3a;
  font-size: 3em;
  font-weight: 300;
  margin: 1% 0 2% 2%;
`;
const ProductTitle = styled.h1`
  color: #3a3a3a;
  font-size: 3em;
  font-weight: 300;
  margin: 0 0 5% 0;
`;

const Paragraph = styled.p`
  width: 70%;
  font-size: 1.2em;
  margin: 0 0 5% 0;
`
const Price = styled.span`
  font-size: 2em;
  margin: 0 0 5% 0;

`

const SpanContainer = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
`
const Text = styled.span`
  font-size: 1.5em;
`
const Selection = styled.select`
width: auto;
font-size: 1em;
height: auto;
margin-left: 1em;
padding: 10px;
`
const Option = styled.option`
  font-size: 1em;
`
const Amount = styled.p`
  font-size: 1.5em;
  margin: 0 3%;

`
const SymbolBtn = styled.button`
  font-size: 1.5em;
  display: flex;
  align-items: center;
  justify-content: center;
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  transition: all 0.2s ease;
  &:hover {
    background-color: #303030;
    color: white;
  border: 2px solid white;
  }
`


const AddToCartBtn = styled.button`
  background: white;
  cursor : pointer;
  color : black;
  border: 2px solid grey;
  padding: .8em;
  width: 100px;
  font-size: .8em;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-left: 5%;
  transition: all 0.3s ease;
  &:hover {
    background-color: #303030;
    color: white;
  border: 2px solid white;
  }

`;

function ProductPage() {

  const [amount, setAmount] = useState(1);

  let amountLimit = true;

  const handleClick = (action) => {
    if (action === "decrease" && amount > 1) {
      setAmount(amount - 1);
    }
    else if (action === "increase" && amount < 5) {
      setAmount(amount + 1);
    }
    else {
      amountLimit = true;
    }
  }

  return (
    <PageContainer>
      <Navbar />
      <PageTitle>Product</PageTitle>
      <Wrapper>
        <Left>
          <ProductImg src='/images/products/product_coat.png' />
        </Left>
        <Right>
          <RightContainer>
            <ProductTitle>Autumn Coat</ProductTitle>
            <Paragraph>Lorem ipsum dolor sit amet consectetur adipisicing elit. Illo veritatis doloremque vel sunt porro, distinctio reprehenderit, nostrum odio totam enim explicabo quasi similique rerum. Praesentium minus magni porro animi amet.</Paragraph>
            <Price>$ 350</Price>
            <BlockContainer>
              <SpanContainer>
                <Text>Size</Text>
                <Selection>
                  <Option selected >S</Option>
                  <Option >M</Option>
                  <Option >L</Option>
                  <Option >XL</Option>
                </Selection>
              </SpanContainer>
            </BlockContainer>
            <BlockContainer>
              <SpanContainer>
                <SymbolBtn onClick={() => handleClick("decrease")} >-</SymbolBtn>
                <Amount>{amount}</Amount>
                <SymbolBtn onClick={() => handleClick("increase")} >+</SymbolBtn>
                <AddToCartBtn>Add to Cart</AddToCartBtn>
              </SpanContainer>
            </BlockContainer>
            <SpanContainer style={{ color: "red" }} >
              {amountLimit && "Sorry can't order more than 5"}
            </SpanContainer>
          </RightContainer>
        </Right>
      </Wrapper>
      <NewsLetter />
      <Footer />

    </PageContainer>
  );
}

export default ProductPage;
