import React from 'react'
import styled from 'styled-components';


const Container = styled.div`
  flex: 1;
  min-width: 20%;
  height: 50%;
  display : flex;
  justify-content: center;
  align-items: center;
  margin : 2px;
  padding: 10px;
  background-color: #e5ecee;
  position: relative;
  transition: all 0.4s ease;
  cursor: pointer;

  &:hover  {
    background-color: #ffffff;
  }
`;

const Img = styled.img`
  width: 70%;
  max-height: 80%;
  object-fit: contain;
  //background-color: white;
  //position : absolute;
  //z-index: 0;

`;

const IconWrapper = styled.div`
  width : 100%;
  height: 100%;
  background-color: none;
  position: absolute;
  bottom: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 2;
  opacity: 0;
  transition: all 0.4s ease;
  
  &:hover {
    opacity: 1;

  }
`;

const AddToCartButton = styled.button`
  background: white;
  cursor : pointer;
  color : black;
  border: 2px solid grey;
  padding: 1em;
  width: 200px;
  font-size: 1em;

`;



export default function Product({ item }) {
  return (
    <Container>
      <Img src={item.imgLink} />
      <IconWrapper>
        <AddToCartButton>
          Add To Cart
        </AddToCartButton>
      </IconWrapper>
    </Container>
  )
}
