import React from 'react'
import styled from 'styled-components'
import { Facebook, Instagram, Twitter, Pinterest } from '@mui/icons-material/';

const Container = styled.div`
  display: flex;
  width: 100%;
  height: 30vh;
  background-color: #ffffff;
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 1%;
`;

const Center = styled.div`
  flex: 1;
  display: flex;
  padding: 1%;
  flex-direction: column;
`;
const Right = styled.div`
  flex: 1;
  display: flex;
  padding: 1%;
`;

const LogoName = styled.h2`
  font-size: 3em;
`;

const Text = styled.p`
  color: black;
  font-size: 1.1em;
  margin-top: 5%;
`;

const IconContainer = styled.div`
  display: flex;
  align-items: center;
  margin-top: 5%;
  width: 50%;
  justify-content: space-between;
`;

const IconBG = styled.div`
  background-color: ${props => props.bgColor};
  color: white;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  display: flex;
  align-items: center;
  justify-content: center;

`;

const SectionTitle = styled.h2`
  font-size: 1.5em;
  font-weight: 700;
`;

const ListContainer = styled.ul`
  margin: .7em 0 0 0;
  padding: 0;
  max-height: 70%;
  display: flex;
  width: 100%;
  flex-direction: column;
  list-style: none;
  flex-wrap: wrap;
`;

const ListItem = styled.li`
  font-size: 1.2em;
  font-weight: 400;
  margin: .7em 0 0 0;
`;







export default function Footer() {
  return (
    <Container>
      <Left>
        <LogoName> e-Commerce</LogoName>
        <Text>Lorem ipsum, dolor sit amet consectetur adipisicing elit. Error, nulla quibusdam? Ullam aliquid, numquam quo ab fugit nulla, repellendus alias vitae iusto eligendi, sit esse et error obcaecati voluptatum nobis.</Text>
        <IconContainer>
          <IconBG bgColor="blue" >
            <Facebook style={{ width: "80%", height: "80%" }} />
          </IconBG>
          <IconBG bgColor="blue">
            <Facebook style={{ width: "80%", height: "80%" }} />
          </IconBG>
          <IconBG bgColor="grey">
            <Facebook style={{ width: "80%", height: "80%" }} />
          </IconBG>
          <IconBG bgColor="grey">
            <Facebook style={{ width: "80%", height: "80%" }} />
          </IconBG>
        </IconContainer>
      </Left>
      <Center>
        <SectionTitle>Useful Links</SectionTitle>
        <ListContainer>
          <ListItem>Home</ListItem>
          <ListItem>Man Fashion</ListItem>
          <ListItem>Accessories</ListItem>
          <ListItem>Order Tracking</ListItem>
          <ListItem>Wishlist</ListItem>
          <ListItem>Cart</ListItem>
          <ListItem>Woman Fashion</ListItem>
          <ListItem>My Account</ListItem>
          <ListItem>Favourites</ListItem>
          <ListItem>Terms</ListItem>
        </ListContainer>
      </Center>
      <Right>Right</Right>
    </Container>
  )
}
