import styled from 'styled-components/';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import Products from '../components/Products';
import NewsLetter from '../components/NewsLetter';


const PageContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  `
const Wrapper = styled.div`
  display: flex;
  width: 100%;
  height: 85vh;
`

const Left = styled.div`
  flex: 1;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: aliceblue;
  
`
const Right = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
`
const RightContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  height: 100%;

`

const ProductImg = styled.img`
  object-fit: contain;
  width: auto;
  height: 90%;
`


const FiltersContainer = styled.div`
  display: flex;
  align-items: center;
  height: 80px;
  width: 100%;
  justify-content: space-between;
  
`

const PageTitle = styled.h1`
  color: #3a3a3a;
  font-size: 3em;
  font-weight: 300;
  margin: 1% 0 2% 2%;
`;
const ProductTitle = styled.h1`
  color: #3a3a3a;
  font-size: 3em;
  font-weight: 300;
  margin: 0 0 5% 0;
`;

const Paragraph = styled.p`
  width: 70%;
  font-size: 1.2em;
  margin: 0 0 5% 0;
`
const Price = styled.span`
  font-size: 2em;
  margin: 0 0 5% 0;

`

const FilterTypeContainer = styled.div`
  display: flex;
  align-items: center;
`
const Text = styled.span`
  font-size: 1.5em;
`
const Selection = styled.select`
width: auto;
font-size: 1em;
height: auto;
margin-left: 1em;
padding: 10px;
`
const Option = styled.option`
  font-size: 1em;
`


function ProductPage() {
  return (
    <PageContainer>
      <Navbar />
      <PageTitle>Product</PageTitle>
      <Wrapper>
        <Left>
          <ProductImg src='/images/products/product_coat.png' />
        </Left>
        <Right>
          <RightContainer>
            <ProductTitle>Autumn Coat</ProductTitle>
            <Paragraph>Lorem ipsum dolor sit amet consectetur adipisicing elit. Illo veritatis doloremque vel sunt porro, distinctio reprehenderit, nostrum odio totam enim explicabo quasi similique rerum. Praesentium minus magni porro animi amet.</Paragraph>
            <Price>$ 350</Price>
            <FiltersContainer>
              <FilterTypeContainer>
                <Text>Filter Products</Text>
                <Selection>
                  <Option disabled selected >Type</Option>
                  <Option >Coats</Option>
                  <Option >Jackets</Option>
                  <Option >Sweaters</Option>
                </Selection>
              </FilterTypeContainer>

            </FiltersContainer>
          </RightContainer>
        </Right>
      </Wrapper>
      <NewsLetter />
      <Footer />

    </PageContainer>
  );
}

export default ProductPage;
