import styled from 'styled-components';
import Product from './Product';
import { productsData } from '../data/productsData';
import { mobile } from '../data/responsive';


const PageWrapper = styled.div`
  margin-top: 1%;
  width : 100%;
  height : 100vh;
  display : flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  
  `;

const Container = styled.div`
  width : 100%;
  height : 100%;
  display : flex;
  //padding : 20px;
  //flex-wrap: wrap;
  align-items: center;
  justify-content: space-between;
  ${mobile({ flexDirection: "column", flexWrap: 'none' })};
`;


export default function Products() {
  return (
    <PageWrapper>
      <Container>
        {productsData.map(el =>
          <Product item={el} key={el.id} />
        )}
      </Container>
    </PageWrapper>

  )
}
