import React from 'react'
import styled from 'styled-components'
import { ArrowLeft, ArrowRight } from '@material-ui/icons/';


const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  //overflow: hidden;
  position: relative;
`
const Arrow = styled.div`
  position: absolute;
  top: 0;
  bottom: 0;
  margin: auto;
  height: 50px;
  width: 50px;
  display:flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  background-color: #e4e4e4;
  left: ${props => props.direction === "left" && "20px"};
  right: ${props => props.direction === "right" && "20px"};
  cursor:pointer;
  opacity: 0.5;
`
const Wrapper = styled.div`
  height: 100%;
  display:flex;
`
const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
`
const ImgContainer = styled.div`
  flex: 1;
  //background-color: #ffffff;
`
const Img = styled.img`
  object-fit: cover;
  height:100%;
  width: 100%;
`
const Right = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
`
const TextContainer = styled.div`
  margin-left: 3%;
  //background-color: #836d66;
  display: flex;
  flex-direction: column;
  justify-content: center;
`
const Title = styled.h1`
  font-weight: 500;
  font-size: 6em;
  color: #2c2c2c;
  margin-bottom: 10%;

`
const Paragraph = styled.p`
  font-size: 1.8em;
  margin-bottom: 10%;
  margin-right: 10%;

`
const ShowButton = styled.button`
  background: none;
  cursor : pointer;
  border: 2px solid grey;
  padding: 1%;
  width: 30%;
  font-size: 1.2em;

`

export default function Slider() {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowLeft />
      </Arrow>
      <Arrow direction="right">
        <ArrowRight />
      </Arrow>
      <Wrapper>
        <Slide>
          <ImgContainer>
            <Img src="/images/slider/slider_coat_ocra.jpg" />
          </ImgContainer>
          <Right>
            <TextContainer>
              <Title>Winter Sales</Title>
              <Paragraph>
                Shop now summer Articles for this week only 20% discount on all products.
              </Paragraph>
              <ShowButton>
                Show More
              </ShowButton>
            </TextContainer>
          </Right>
        </Slide>

        <Slide>
          <ImgContainer>
            <Img src="/images/slider/slider_coat_ocra.jpg" />
          </ImgContainer>
          <Right>
            <TextContainer>
              <Title>Winter Sales</Title>
              <Paragraph>
                Shop now summer Articles for this week only 20% discount on all products.
              </Paragraph>
              <ShowButton>
                Show More
              </ShowButton>
            </TextContainer>
          </Right>
        </Slide>

      </Wrapper>

    </Container>
  )
}
