import styled from 'styled-components';
import Product from './Product';
import { productsData } from '../data/productsData';
import { mobile } from '../data/responsive';
import { useEffect, useState } from 'react';
import { LineAxisOutlined } from '@mui/icons-material';
import axios from 'axios';


const PageWrapper = styled.div`
  margin-top: 1%;
  width : 100%;
  height : 100vh;
  display : flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  ${mobile({ height: "auto" })};

  
  `;

const Container = styled.div`
  width : 100%;
  height : 100%;
  display : flex;
  //padding : 20px;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-between;
  ${mobile({ flexDirection: "column", flexWrap: 'nowrap' })};
`;


export default function Products({ filters, sort, category }) {
  console.log(category);
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    const getProducts = async () => {
      try {
        const res = await axios.get(category ? `http://localhost:5000/api/products/${category}` : `http://localhost:5000/api/products`)
        setProducts(res)
      } catch (err) {

      }
    }
    getProducts();
  }, [category])

  useEffect(() => {
    setFilteredProducts(
      products.filter(el => Object.entries(filters).every(([key, value]) => el[key].includes(value)))
    )
  }, [filters, sort, category])

  return (
    <PageWrapper>
      <Container>
        {filteredProducts.map(el => <Product item={el} key={el.id} />)}
      </Container>
    </PageWrapper>

  )
}
